INSERT INTO `vertraege_klauseln` (`id`, `mitarbeiter_id`, `timestamp`, `gruppen_id`, `klausel_referenz_id`, `status_id`, `text`, `standard`, `version`, `auschluss_klassen`, `geloescht`) VALUES
(1, 11, '2022-08-25 11:43:13', 1, 1, 2, '<p>Lieferung des Mietgegenstandes.</p>', 0, 1, NULL, 0),
(2, 11, '2022-08-25 11:43:13', 1, 2, 2, '<p>Zwei Einweisungen in die Bedienung des Gerätes.</p>', 0, 1, NULL, 0),
(3, 11, '2022-08-25 11:43:13', 1, 3, 2, '<p>Telefonische Auskunft.</p>', 0, 1, NULL, 0),
(4, 11, '2022-08-25 11:43:13', 1, 4, 2, '<p>Erstinstallation im Netzwerk ab Netzwerkdose am PC oder per USB.</p>', 0, 1, NULL, 0),
(5, 11, '2022-08-25 11:43:13', 1, 5, 2, '<p>Urheberrechtsangaben</p>', 0, 1, NULL, 0),
(6, 11, '2022-08-25 11:43:13', 2, 6, 2, '\r\n<p>Alle Verbrauchsmaterialien ausgenommen Papier, Heftklammern und Stempel.</p>\r\n', 0, 1, NULL, 0),
(7, 11, '2022-08-25 11:43:13', 2, 7, 2, '\r\n<p>Kleebetiketten DIN A5.</p>\r\n', 0, 1, NULL, 0),
(8, 11, '2022-08-25 11:43:13', 2, 8, 2, '\r\n<p>Alle Ersatzteile.</p>', 0, 1, NULL, 0),
(9, 11, '2022-08-25 11:43:13', 2, 9, 2, '\r\n<p>Technikerarbeitszeiten (Technischer Service vor Ort - Next Business Day).</p>', 0, 1, NULL, 0),
(10, 11, '2022-08-25 11:43:13', 2, 10, 2, '\r\n<p>Anfahrt / Abfahrt</p>\r\n', 0, 1, NULL, 0),
(11, 11, '2022-08-25 11:43:13', 2, 11, 2, '<p>Bei einem irreparablen Defekt Ihres Gerätes, erhalten Sie ein gleichwertiges Ersatzgerät.\nAusgenommen sind Defekte durch Vandalismus, Überspannung oder Elementarschäden\n(siehe 5 6 Pflichten des Kunden).</p>', 0, 1, NULL, 0),
(12, 11, '2022-08-25 11:43:13', 3, 12, 2, '<p>Benutzerfehler, unsachgemäße Bedienung, insbesondere beim Entfernen von Papierstau.</p>', 0, 1, NULL, 0),
(13, 11, '2022-08-25 11:43:13', 3, 13, 2, '<p>Tauschen von Verbracuhsmaterialien vor Ablauf der Laufzeit.</p>', 0, 1, NULL, 0),
(14, 11, '2022-08-25 11:48:43', 3, 14, 2, '<p>Falsches Einsetzen oder Verwendung von falschem Material.</p>', 0, 1, NULL, 0),
(15, 11, '2022-08-25 11:48:43', 3, 15, 2, '<p>Fehler aufgrund von Fremdkörper, wie z.B. Klebe-Etiketten, Büroklammern, Heftklammern usw.im Gerät.</p>', 0, 1, NULL, 0),
(16, 11, '2022-08-25 11:48:43', 3, 16, 2, '<p>Neuinstallation/ Neukonfiguration aufgrund von IT- oder TK-Veränderung des Vertragsnehmers.</p>', 0, 1, NULL, 0),
(17, 11, '2022-08-25 11:48:43', 3, 17, 2, '<p>Fehler mit Ursache in der hauseigenen IT- oder TK-Peripherie des Vertragsnehmers.</p>', 0, 1, NULL, 0),
(18, 11, '2022-08-25 11:48:43', 4, 18, 2, '<p>Verbrauchsmaterial bestellen, wechseln und bis zur Verwendung originalverpackt aufbewahren.</p>', 0, 1, NULL, 0),
(19, 11, '2022-08-25 11:48:43', 4, 19, 2, '<p>Übermittlung der Zählerstände zu den vereinbarten Zeitpunkten.</p>', 0, 1, NULL, 0),
(20, 11, '2022-08-25 11:48:43', 4, 20, 2, '<p>Vertragsgegenstand vor Vandalismus, Überspannung und Elementarschäden schützen oder dagegen mitversichern.</p>', 0, 1, NULL, 0),
(21, 11, '2022-08-25 11:48:43', 4, 21, 2, '<p>Enthält Ihr Vertrag keine Mindestabnahmemenge, so versichern Sie uns, an gleichem Standort, für gleiche Aufgaben, keine Geräte eines Mitbewerbers aufstellen zu lassen.</p>', 0, 1, NULL, 0),
(22, 11, '2022-08-25 11:48:43', 4, 22, 2, '<p>Mit der letzten Abfrage der Zählerstände im Jahr, Auskunft über die Tonervorräte erteilen.</p>', 0, 1, NULL, 0),
(23, 11, '2022-08-25 11:48:43', 4, 23, 2, '<p>Rückgabe des Vertragsgegenstandes erfolgt am Ende der Mietlaufzeit durch den Miete inklusive Kabel, nicht verbrauchten oder im Gerät befindlichen Verbrauchsmaterialien und Bedienungsanleitungen in gepflegtem Zustand.</p>', 0, 1, NULL, 0),
(24, 11, '2022-08-25 11:48:43', 5, 24, 2, '<p>Nach Ablauf der <custom class=\"mietlaufzeit\"></custom> Vertragslaufzeit verringert sich der monatliche Mietpreis für die Geräte um jeweils 50% des Nettomietpreises. </p>', 0, 1, NULL, 0),
(25, 11, '2022-08-25 11:48:43', 5, 25, 2, '<p>Tonerbestellung und Zählerstandsmeldung (sieht §<custom class=\"pflicht_des_kunden\"></custom>) Pflicht des Kunden) müssen nicht gemeldet werden, sondern erfolgen automatisiert per MPS Software FM Audit. </p>', 0, 1, NULL, 0),
(26, 11, '2022-08-25 11:48:43', 5, 26, 2, '<p>Sämtliche Inhalte und Anforderungen der Ausschreibung und Leistungsbeschreibung sind Bestandteil dieses Vertrages.</p>', 0, 1, NULL, 0),
(27, 11, '2022-08-25 11:48:43', 6, 27, 2, '<p>Der Vertragsgeber bietet nach Laufzeitende des Vertrages eines Festplattenlöschung bzw. eine Löschung der Verzeichnisse des Vertragsgegenstandes an. Dieser Service ist für den Vertragsnehmer kostenfrei, insofern er beim Vertragsnehmer nach Laufzeitende einen neuen Vertrag abschließt bzw. ein neues Gerät kauft. Ansonsten erstellt der Vertragsgeber zum Vertragsende gerne ein Angebot über die Festplattenlöschung.</p>', 0, 1, NULL, 0),
(28, 11, '2022-08-25 11:48:43', 6, 28, 2, '<p>Nach Abholung der Geräte erbringt der Vertragsgeber ein Nachweis, dass die Festplatten der Geräte konform mit den Vorgaben des hessischen Datenschutzes gelöscht wurde.</p>', 0, 1, NULL, 0),
(29, 11, '2022-08-25 11:48:43', 7, 29, 2, '<p>Mahnungen aufgrund versäumter Zahlungsziele werden mit 5,00 € in Rechnung gestellt, gleiches gilt bei Rückbuchungen im Lastschriftverfahren. Bei Zahlungsverzug behält sich der Vertragsgeber vor, Warenlieferung und Serviceleistung einzustellen, bis alle offen stehenden Rechnungen ausgeglichen sind.</p>', 0, 1, NULL, 0),
(30, 11, '2022-08-25 12:06:26', 8, 30, 2, '<p>Der Gerichtsstand ist Fulda.</p>', 0, 1, NULL, 0);
